mapper.select:
(select distinct suser.id,login_name,corporater,corporater_mobile,name,address,reseller_type,suser.create_date,account_state,province,city,county from sys_user suser left join sys_label_relation channelUser on suser.id=channelUser.rel_id
left join sys_user_relation userRel on suser.id=userRel.rel_user_id
left join customer_extends custext on suser.id=custext.customer_id
)
union ( select id,login_name,corporater,corporater_mobile,name,address,reseller_type,create_date,account_state,province,city,county from sys_user suser
and id in
#{resellerId}
)

(select distinct suser.id,login_name,corporater,corporater_mobile,name,address,reseller_type,suser.create_date,account_state,province,city,county from sys_user suser left join sys_user_relation userRel on suser.id=userRel.rel_user_id
left join customer_extends custext on suser.id=custext.customer_id
)
union ( select id,login_name,corporater,corporater_mobile,name,address,reseller_type,create_date,account_state,province,city,county from sys_user suser
and id in
#{resellerId}
)

sql.if:
and suser.id not in ( select rel_id from sys_label_relation where status=1 and obj_id=#{channelReseller.channelId}
and rel_type = #{channelReseller.relType}
)

mapper.sql:
where
	account_state = 1
and 
	check_status=1

	and login_name like CONCAT(CONCAT('%', #{channelReseller.loginName}),'%')


	and (name like CONCAT(CONCAT('%', #{channelReseller.name}),'%') or supplier_normal like CONCAT(CONCAT('%', #{channelReseller.name}),'%'))


	and corporater like CONCAT(CONCAT('%', #{channelReseller.concat}),'%')


	and corporater_mobile = #{channelReseller.mobile}


	and address like CONCAT(CONCAT('%', #{channelReseller.address}),'%')


	and reseller_type = #{channelReseller.resellerType}


	and user_type = #{channelReseller.userType}


	and province=#{channelReseller.province}


	and city=#{channelReseller.city}


	and county=#{channelReseller.county}


	and is_root = #{channelReseller.isRoot}



	and suser.create_date >= #{channelReseller.bindSDate}




	and suser.create_date <= #{channelReseller.bindEDate}



	and custext.referee_id = #{channelReseller.refereeId}


	and suser.id not in

	#{removeId}

where
	suser.account_state = 1
and 
	suser.check_status=1



		and suser.login_name like concat('%',#{reseller.loginName},'%')


		and (suser.name like CONCAT(CONCAT('%', #{reseller.name}),'%') or suser.supplier_normal like CONCAT(CONCAT('%', #{reseller.name}),'%'))


		and (
			suser.name like CONCAT(CONCAT('%', #{reseller.nameOrCorporaterOrMobile}),'%')
			or suser.corporater like CONCAT(CONCAT('%', #{reseller.nameOrCorporaterOrMobile}),'%')
			or suser.corporater_mobile like CONCAT(#{reseller.nameOrCorporaterOrMobile},'%')
		)


		and suser.corporater like CONCAT(CONCAT('%', #{reseller.corporater}),'%')


		and suser.corporater_mobile = #{reseller.corporaterMobile}


		and suser.address like CONCAT(CONCAT('%', #{reseller.address}),'%')


		and suser.reseller_type = #{reseller.resellerType}


		and suser.user_type = #{reseller.userType}


		and suser.create_date = DATE_FORMAT(#{reseller.createDate}, '%Y-%m-%d')


		and suser.is_root = #{reseller.isRoot}


		and suser.province=#{reseller.province}


		and suser.city=#{reseller.city}


		and suser.county=#{reseller.county}



	and suser.create_date >= #{reseller.bindDateBegin}




	and suser.create_date <= #{reseller.bindDateEnd}



		and custext.referee_id = #{reseller.refereeId}



			AND suser.id IN

				#{_userId, jdbcType=BIGINT}



			AND suser.id NOT IN

				#{_userId, jdbcType=BIGINT}

mapper.select:
select count(id) from sys_user sur where
and sur.account_state = 1 and sur.user_type in (6, 1, 7, 13) and sur.is_root = 1
and sur.name like #{reseller.name, jdbcType=VARCHAR}
and sur.id in
#{id, jdbcType=BIGINT}
or 1 = 2

select id, name, corporater from sys_user sur where
and sur.account_state = 1 and sur.user_type in (6, 1, 7, 13) and sur.is_root = 1
and sur.name like #{reseller.name, jdbcType=VARCHAR}
and sur.id in
#{id, jdbcType=BIGINT}
or 1 = 2
